CREATE TABLE `t_tag` (
	`id` bigint(10) NOT NULL AUTO_INCREMENT COMMENT '主键',
	`name` varchar(20) NOT NULL DEFAULT '' COMMENT 'tag名称',
	`enable` tinyint(2) NOT NULL DEFAULT 0 COMMENT '状态',
	`update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',
	PRIMARY KEY (`id`)
) ENGINE=`INNODB` AUTO_INCREMENT=1 DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ROW_FORMAT=DYNAMIC COMMENT='新闻tags' CHECKSUM=0 DELAY_KEY_WRITE=0;